var http = require('http-browserify');

var requestFunc = http.request;
http.request = function(params, cb) {
	if (!params.scheme) {
		debugger;
		params.scheme = 'https';
	}
	var res = requestFunc(params, function(a) {
		a.setEncoding = function() {};
		return cb(a);
	});
	res.setEncoding = function() {};
	return res;
}

module.exports = http;